mapper.insert:


INSERT INTO CLAIM   /* QueryID : com.demo.proworks.claim.insertClaim */
( 
    CLAIM_NO
     , CLAIM_TYPE       
     , RECEIPT_DATE     
     , STATUS           
     , EMP_NO           
     , ID               
     , CLAIM_CONTENT    
     , DISEASE_CODE     
     , DATE_OF_ACCIDENT 
)
VALUES  
( 
    #{claim_no}
     , #{claim_type}       
     , #{receipt_date}     
     , #{status}           
     , #{emp_no}           
     , #{ID}               
     , #{claim_content}    
     , #{disease_code}     
     , #{date_of_accident}  
)

mapper.update:


  UPDATE CLAIM    /* QueryID : com.demo.proworks.claim.updateClaim */      
	  SET  
          CLAIM_NO         = #{claim_no}                	                       
       , CLAIM_TYPE       = #{claim_type}       
       , RECEIPT_DATE     = #{receipt_date}     
       , STATUS           = #{status}           
       , EMP_NO           = #{emp_no}           
       , ID               = #{ID}               
       , CLAIM_CONTENT    = #{claim_content}    
       , DISEASE_CODE     = #{disease_code}     
       , DATE_OF_ACCIDENT = #{date_of_accident}                  
  WHERE   
          CLAIM_NO         = #{claim_no}

mapper.select:
SELECT /* QueryID : com.demo.proworks.claim.selectListClaim */ CLAIM_NO, CLAIM_TYPE, RECEIPT_DATE, STATUS, EMP_NO, ID, CLAIM_CONTENT, DISEASE_CODE, DATE_OF_ACCIDENT FROM ( SELECT S.*, CEIL( (@ROWNUM:=@ROWNUM+1) / #{pageSize} ) AS PAGE FROM ( SELECT * FROM CLAIM WHERE 1=1
AND RECEIPT_DATE like '%${SC_receipt_date}%'
AND ID like '%${SC_ID}%'
) S, (SELECT @ROWNUM := 0) TMP ) A WHERE PAGE = #{pageIndex}
