mapper.select:
SELECT claim_no, user_name, receipt_date, deadline, emp_name, status, emp_no, ID FROM ( SELECT S.*, CEIL((@ROWNUM := @ROWNUM + 1) / #{pageSize}) AS PAGE FROM ( SELECT c.claim_no, u.user_name, c.receipt_date, DATE_ADD(c.receipt_date, INTERVAL 3 DAY) AS deadline, reg.emp_name, c.status, c.emp_no, c.ID FROM CLAIM c JOIN USER u ON c.ID = u.ID JOIN EMPLOYEE reg ON c.emp_no = reg.emp_no JOIN APPROVAL_REQ ar ON c.claim_no = ar.claim_no WHERE ar.manager_no = #{emp_no}
AND c.status = #{status}
AND c.status != 'ê²°ìž¬ì¤‘'
) S, (SELECT @ROWNUM := 0) TMP ) A WHERE PAGE = #{pageIndex} ORDER BY receipt_date ASC;

