mapper.update:


  UPDATE CLAIM    /* QueryID : com.demo.proworks.claim.updateClaim */      
	  SET  
          CLAIM_NO         = #{claim_no}                	                       
       , CLAIM_TYPE       = #{claim_type}       
       , RECEIPT_DATE     = #{receipt_date}     
       , STATUS           = #{status}           
       , EMP_NO           = #{emp_no}           
       , ID               = #{ID}               
       , CLAIM_CONTENT    = #{claim_content}    
       , DISEASE_CODE     = #{disease_code}     
       , DATE_OF_ACCIDENT = #{date_of_accident} 
       , DISASTER_CODE     = #{disaster_code}                      
  WHERE   
          CLAIM_NO         = #{claim_no}

mapper.select:
SELECT /* QueryID : com.demo.proworks.claim.selectListClaim NOCACHE */ CLAIM_NO, CLAIM_TYPE, RECEIPT_DATE, STATUS, EMP_NO, ID, CLAIM_CONTENT, DISEASE_CODE, DATE_OF_ACCIDENT, DISASTER_CODE, NOW() as CACHE_BUSTER FROM ( SELECT S.*, CEIL( (@ROWNUM:=@ROWNUM+1) / #{pageSize} ) AS PAGE FROM ( SELECT * FROM CLAIM WHERE 1=1
AND RECEIPT_DATE like '%${SC_receipt_date}%'
AND ID like '%${SC_ID}%'
) S, (SELECT @ROWNUM := 0) TMP ORDER BY RECEIPT_DATE DESC ) A WHERE PAGE = #{pageIndex}

SELECT /* QueryID : com.demo.proworks.claim.selectClaimEmployeeList */ c.claim_no, c.claim_type, e.emp_name, c.status FROM CLAIM c LEFT JOIN EMPLOYEE e ON c.emp_no = e.emp_no WHERE 1=1
AND c.claim_type = #{SC_claim_type}
AND c.status = #{SC_status}
AND e.emp_name LIKE CONCAT('%', #{SC_emp_name}, '%')
ORDER BY c.claim_no DESC

SELECT /* QueryID : com.demo.proworks.claim.selectClaimEmployeeListCount */ COUNT(*) as totcnt FROM CLAIM c LEFT JOIN EMPLOYEE e ON c.emp_no = e.emp_no WHERE 1=1
AND c.claim_type = #{SC_claim_type}
AND c.status = #{SC_status}
AND e.emp_name LIKE CONCAT('%', #{SC_emp_name}, '%')

SELECT /* QueryID : com.demo.proworks.claim.selectClaimFullJoinListCount */ COUNT(*) as totcnt FROM CLAIM c LEFT JOIN USER u ON c.ID = u.ID LEFT JOIN EMPLOYEE e ON c.emp_no = e.emp_no LEFT JOIN CLAIM_RESULT r ON c.claim_no = r.claim_no WHERE 1=1
AND c.claim_type = #{SC_claim_type}
AND c.status = #{SC_status}
AND e.emp_name LIKE CONCAT('%', #{SC_emp_name}, '%')
AND u.user_name LIKE CONCAT('%', #{SC_user_name}, '%')

SELECT /* QueryID : com.demo.proworks.claim.selectUserClaimsByRrn */ c.claim_no, c.claim_type, u.user_name, r.amount, e.emp_name, c.status, c.emp_no, c.ID FROM CLAIM c LEFT JOIN USER u ON c.ID = u.ID LEFT JOIN EMPLOYEE e ON c.emp_no = e.emp_no LEFT JOIN CLAIM_RESULT r ON c.claim_no = r.claim_no WHERE 1=1
AND c.ID = #{id}
AND c.claim_type = #{SC_claim_type}
ORDER BY c.claim_no DESC
