mapper.select:
/* QueryID: com.demo.proworks.dashboard.selectMyUrgentClaimCount (나의 긴급 처리 건수 조회) */
WITH RECURSIVE BusinessDays AS ( SELECT c.claim_no, c.emp_no, (SELECT ar.manager_no FROM APPROVAL_REQ ar WHERE ar.claim_no = c.claim_no LIMIT 1) AS manager_no, c.receipt_date, c.receipt_date AS next_day, 0 AS business_day_count FROM CLAIM c WHERE c.status NOT IN ('완료', '결재완료') UNION ALL SELECT b.claim_no, b.emp_no, b.manager_no, b.receipt_date, DATE_ADD(b.next_day, INTERVAL 1 DAY), b.business_day_count + CASE WHEN DAYOFWEEK(DATE_ADD(b.next_day, INTERVAL 1 DAY)) IN (1, 7) THEN 0 WHEN EXISTS (SELECT 1 FROM TBL_HOLIDAYS h WHERE h.holiday_date = DATE(DATE_ADD(b.next_day, INTERVAL 1 DAY))) THEN 0 ELSE 1 END FROM BusinessDays b WHERE b.business_day_count < 3 ), Deadlines AS ( SELECT claim_no, emp_no, manager_no, MIN(next_day) AS deadline_date FROM BusinessDays WHERE business_day_count = 3 GROUP BY claim_no, emp_no, manager_no ) SELECT COUNT(*) FROM Deadlines d WHERE d.deadline_date <= DATE_ADD(NOW(), INTERVAL 24 HOUR)
AND d.emp_no = #{empNo}
AND d.manager_no = #{empNo}
