mapper.insert:


INSERT INTO PAYMENT_INFO   /* QueryID : com.demo.proworks.paymentinfo.insertPaymentInfo */
( 
    TREATMENT_ID
    , CLAIM_NO
     , TOTAL_MEDICAL_FEE     
     , TOTAL_PATIENT_PAYMENT 
     , AMOUNT_PAID           
     , DUE_AMT               
     , DUE_PAY               
)
VALUES  
( 
    #{treatment_id}
    , #{claim_no}
     , #{total_medical_fee}     
     , #{total_patient_payment} 
     , #{amount_paid}           
     , #{due_amt}               
     , #{due_pay}                
)

mapper.select:
SELECT /* QueryID : com.demo.proworks.paymentinfo.selectListPaymentInfo */ TOTAL_MEDICAL_FEE, TOTAL_PATIENT_PAYMENT, AMOUNT_PAID, DUE_AMT, DUE_PAY, TREATMENT_ID, CLAIM_NO FROM ( SELECT S.*, CEIL( (@ROWNUM:=@ROWNUM+1) / #{pageSize} ) AS PAGE FROM ( SELECT * FROM PAYMENT_INFO WHERE 1=1
AND TREATMENT_ID like '%${SC_treatment_id}%'
AND CLAIM_NO like '%${SC_claim_no}%'
) S, (SELECT @ROWNUM := 0) TMP ) A WHERE PAGE = #{pageIndex}
