mapper.insert:


INSERT INTO TREATMENT   /* QueryID : com.demo.proworks.treatment.insertTreatment */
( 
    TREATMENT_ID
    , CLAIM_NO
     , DEPARTMENT     
     , TREATMENT_DATE 
     , PATIENT_TYPE   
     , HOSPITAL_NAME  
     , HOSPITAL_GRADE 
)
VALUES  
( 
    #{treatment_id}
    , #{claim_no}
     , #{department}     
     , #{treatment_date} 
     , #{patient_type}   
     , #{hospital_name}  
     , #{hospital_grade}  
)

mapper.update:


  UPDATE TREATMENT    /* QueryID : com.demo.proworks.treatment.updateTreatment */      
	  SET  
          TREATMENT_ID   = #{treatment_id}  
   ,  CLAIM_NO       = #{claim_no}              	                       
       , DEPARTMENT     = #{department}     
       , TREATMENT_DATE = #{treatment_date} 
       , PATIENT_TYPE   = #{patient_type}   
       , HOSPITAL_NAME  = #{hospital_name}  
       , HOSPITAL_GRADE = #{hospital_grade}                  
  WHERE   
          TREATMENT_ID   = #{treatment_id}  
   AND CLAIM_NO       = #{claim_no}

mapper.select:
SELECT /* QueryID : com.demo.proworks.treatment.selectListTreatment */ TREATMENT_ID, CLAIM_NO, DEPARTMENT, TREATMENT_DATE, PATIENT_TYPE, HOSPITAL_NAME, HOSPITAL_GRADE FROM ( SELECT S.*, CEIL( (@ROWNUM:=@ROWNUM+1) / #{pageSize} ) AS PAGE FROM ( SELECT * FROM TREATMENT WHERE 1=1
AND TREATMENT_ID like '%${SC_treatment_id}%'
AND CLAIM_NO like '%${SC_claim_no}%'
AND TREATMENT_DATE like '%${SC_treatment_date}%'
) S, (SELECT @ROWNUM := 0) TMP ) A WHERE PAGE = #{pageIndex}
