head.script:
	scwin.onpageload = function() {
		// 배정규칙 readOnly false

		// 버튼 숨김
		btn_save.show("");   // 저장 버튼 
		btn_edit.hide();     // 수정 버튼 제거
		btn_delete.hide();   // 삭제 버튼 제거

		//search_box
        var cptIbxName = $p.getComponentById("searchComp_1");
        if(cptIbxName == undefined){
			search_box.hide();
        }

		// 배정규칙 조회
		scwin.btn_search_onclick();
	};

	/************************** 이벤트 함수 *************************/

	/**
	 * 조회 버튼 클릭 이벤트
	 */		   
	scwin.btn_search_onclick = function(e) {
		// PGAE번호 초기화
		dmp_assignRuleVo.set("pageIndex","1");
		pgl_assignRuleList.setSelectedIndex(1);

		// 서버통신 - 배정규칙 조회
		$c.sbm.execute($p, sbm_selectAssignRuleVoList);
	};

	scwin.sbm_assignRuleList_submitdone = function(e) {
		// error 수신시
		var elData = e.responseJSON.elData;
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}

		// 배정규칙 dataMap 초기화
		dmp_assignRuleVoDetail.setEmptyValue();

		// 배정규칙 readOnly false
		grp_assignRuleInfo.setReadOnly(false);

		// 버튼 숨김
		btn_save.show("");   // 저장 버튼
		btn_edit.hide();     // 수정 버튼 제거
		btn_delete.hide();   // 삭제 버튼 제거		

		// 총 레코드 수 업데이트
        spn_listCnt.setLabel(elData.totalCount);

        // 데이터를 그리드에 셋팅
        dlt_assignRuleVoList.setJSON(elData.assignRuleVoList);

        // 페이지 네비게이터 업데이트
        var totalPageCount = Math.ceil(elData.totalCount / dmp_assignRuleVo.get("pageSize"));
        pgl_assignRuleList.setCount(totalPageCount);

        spn_registerFlag.setValue("등록");

		// 페이징 정보 업데이트
        spn_totalPageCount.setValue(totalPageCount);
        spn_pageIndex.setValue(dmp_assignRuleVo.get("pageIndex"));
        pgl_assignRuleList.setSelectedIndex(dmp_assignRuleVo.get("pageIndex"));
	};	

	/**
	 * 페이지 번호 클릭 이벤트
	 */
	scwin.pgl_assignRuleList_onclick = function(idx) {
		// 페이지 번호 세팅
		dmp_assignRuleVo.set("pageIndex", idx);

		// 서버통신 - 배정규칙 조회
		$c.sbm.execute($p, sbm_selectAssignRuleVoList);
	};

	// 배정규칙 clear
	scwin.btn_reset_onclick = function(e) {
		// 배정규칙 dataMap 초기화
		dmp_assignRuleVoDetail.setEmptyValue();
		// 배정규칙 readOnly false
		grp_assignRuleInfo.setReadOnly(false);

		btn_save.show("");   // 저장 버튼 생성
		btn_edit.hide();     // 수정 버튼 제거
		btn_delete.hide();   // 삭제 버튼 제거

		spn_registerFlag.setValue("등록");
	};

	/**
	 * 그리드 셀 클릭 이벤트
	 */
	scwin.grd_list_oncellclick = function(row,col,colId) {
		dmp_assignRuleVoDetail.setJSON(dlt_assignRuleVoList.getRowJSON(row));

		btn_save.hide(); 		// 저장 버튼 제거
		btn_edit.show("");      // 수정 버튼 생성
		btn_delete.show("");    // 삭제 버튼 생성

		spn_registerFlag.setValue("수정");
	};

	/**
	 * 저장 버튼 클릭 이벤트
	 */	
	scwin.btn_save_onclick = function(e) {
		// 유효성 체크
		var valInfo = [
			{ id: "rule_id", mandatory: true }
		];

		if ($c.data.validateGroup(grp_assignRuleInfo, valInfo) === true) {
			var promise = $c.win.confirm("저장하시겠습니까?");
			promise.then(function(result) {  
				if (result){
					$c.sbm.execute(sbm_insertAssignRuleVo);
				}
			})
		} 	
	};

	/**
	 * 사원정보 등록 콜백
	 */
	scwin.sbm_insertAssignRuleVo_submitdone = function(e) {
		// error 수신시
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}else{
			// 등록되었습니다. msg 출력
			$c.win.alert("등록되었습니다.");
		}

		// 재조회
		scwin.btn_search_onclick();
	};

	/**
	 * 수정 버튼 클릭 이벤트
	 */		
	scwin.btn_edit_onclick = function(e) {
		// 유효성 체크
		var valInfo = [
			{ id: "rule_id", mandatory: true }
		];

		if ($c.data.validateGroup(grp_assignRuleInfo, valInfo) === true) {
			var promise = $c.win.confirm("수정하시겠습니까?");
			promise.then(function(result) {  
				if (result){
					$c.sbm.execute(sbm_updateAssignRuleVo);
				}
			})
		} 
	};

	/**
	 * 사원정보 수정 콜백
	 */
	scwin.sbm_updateAssignRuleVo_submitdone = function(e) {
		// error 수신시
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}else{
			// 수정되었습니다. msg 출력
			$c.win.alert("수정되었습니다.");
		}
		// 재조회
		scwin.btn_search_onclick();
	};

	/**
	 * 삭제 버튼 클릭 이벤트
	 */		
	scwin.btn_delete_onclick = function(e) {
		// 유효성 체크
		var valInfo = [
			{ id: "rule_id", mandatory: true }
		];

		if ($c.data.validateGroup(grp_assignRuleInfo, valInfo) === true) {
			var promise = $c.win.confirm("삭제하시겠습니까?");
			promise.then(function(result) {  
				if (result){
					$c.sbm.execute(sbm_deleteAssignRuleVo);
				}
			})
		}	
	};

	/**
	 * 삭제 콜백
	 */
	scwin.sbm_deleteAssignRuleVo_submitdone = function(e) {
		// error 수신시
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}else{
			// 삭제되었습니다. msg 출력
			$c.win.alert("삭제되었습니다.");
		}

		// 재조회
		scwin.btn_search_onclick();
	};

	/************************** 자동 배정 관련 함수 *************************/

	/**
	 * 단일 청구 자동 배정 버튼 클릭 이벤트
	 */
	scwin.btn_assignSingleClaim_onclick = function(e) {
		var claimNo = inp_claimNo.getValue();
		if (!claimNo || claimNo.trim() === "") {
			$c.win.alert("청구 번호를 입력해주세요.");
			return;
		}

		// claimNo를 keyword 필드에 설정 (Controller에서 keyword를 claimNo로 사용)
		dmp_autoAssign.set("keyword", claimNo.trim());

		var promise = $c.win.confirm("청구번호 '" + claimNo + "'를 자동 배정하시겠습니까?");
		promise.then(function(result) {
			if (result) {
				$c.sbm.execute($p, sbm_assignSingleClaim);
			}
		});
	};

	/**
	 * 단일 청구 자동 배정 콜백
	 */
	scwin.sbm_assignSingleClaim_submitdone = function(e) {
		var response = e.responseJSON;
		var elHeader = response.elHeader;

		if (elHeader && elHeader.resSuc === false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return;
		}

		var result = response.elData || response;
		if (result.success) {
			$c.win.alert("배정 완료: " + result.message);
			// 청구 번호 필드 초기화
			inp_claimNo.setValue("");
		} else {
			$c.win.alert("배정 실패: " + result.message);
		}
	};

	/**
	 * 전체 청구 일괄 배정 버튼 클릭 이벤트
	 */
	scwin.btn_assignAllClaims_onclick = function(e) {
		var promise = $c.win.confirm("모든 미배정 청구를 자동 배정하시겠습니까?\n시간이 다소 소요될 수 있습니다.");
		promise.then(function(result) {
			if (result) {
				$c.sbm.execute($p, sbm_assignAllClaims);
			}
		});
	};

	/**
	 * 전체 청구 일괄 배정 콜백
	 */
	scwin.sbm_assignAllClaims_submitdone = function(e) {
		var response = e.responseJSON;
		var elHeader = response.elHeader;

		if (elHeader && elHeader.resSuc === false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return;
		}

		var result = response.elData || response;
		if (result.success && result.results) {
			// 결과 목록을 그리드에 표시
			var resultData = [];
			for (var i = 0; i < result.results.length; i++) {
				resultData.push({resultMessage: result.results[i]});
			}
			dlt_assignResults.setJSON(resultData);

			// 결과 표시 영역을 보이게 함
			grp_assignResults.show();

			$c.win.alert("일괄 배정 완료!\n총 " + result.totalProcessed + "건 처리되었습니다.\n세부 결과는 아래 목록을 확인해주세요.");
		} else {
			$c.win.alert("일괄 배정 실패: " + result.message);
		}
	};

	/**
	 * 키워드로 부서 찾기 버튼 클릭 이벤트
	 */
	scwin.btn_findDept_onclick = function(e) {
		var keyword = inp_keyword.getValue();
		if (!keyword || keyword.trim() === "") {
			$c.win.alert("키워드를 입력해주세요.");
			return;
		}

		dmp_autoAssign.set("keyword", keyword.trim());
		$c.sbm.execute($p, sbm_findDeptByKeyword);
	};

	/**
	 * 키워드로 부서 찾기 콜백
	 */
	scwin.sbm_findDeptByKeyword_submitdone = function(e) {
		var response = e.responseJSON;
		var elHeader = response.elHeader;

		if (elHeader && elHeader.resSuc === false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return;
		}

		var result = response.elData || response;
		if (result.success) {
			$c.win.alert("부서 조회 성공!\n부서 ID: " + result.deptId);
			spn_foundDeptId.setValue("부서 ID: " + result.deptId);
		} else {
			$c.win.alert("부서 조회 실패: " + result.message);
			spn_foundDeptId.setValue("");
		}
	};

	/**
	 * 키워드로 배정 가능한 직원 조회 버튼 클릭 이벤트
	 */
	scwin.btn_getAvailableEmployees_onclick = function(e) {
		var keyword = inp_keyword.getValue();
		if (!keyword || keyword.trim() === "") {
			$c.win.alert("키워드를 입력해주세요.");
			return;
		}

		dmp_autoAssign.set("keyword", keyword.trim());
		$c.sbm.execute($p, sbm_getAvailableEmployees);
	};

	/**
	 * 배정 가능한 직원 조회 콜백
	 */
	scwin.sbm_getAvailableEmployees_submitdone = function(e) {
		var response = e.responseJSON;
		var elHeader = response.elHeader;

		if (elHeader && elHeader.resSuc === false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return;
		}

		var result = response.elData || response;
		if (result.success && result.employees) {
			var empInfo = "배정 가능한 직원 (" + result.totalCount + "명):\n";
			for (var i = 0; i < result.employees.length; i++) {
				var emp = result.employees[i];
				empInfo += "- " + emp.ename + " (" + emp.empno + ")\n";
			}
			$c.win.alert(empInfo);
		} else {
			$c.win.alert("직원 조회 실패: " + result.message);
		}
	};

	/**
	 * 배정 결과 목록 닫기
	 */
	scwin.btn_closeResults_onclick = function(e) {
		grp_assignResults.hide();
		dlt_assignResults.removeAll();
	};
