head.script:
	scwin.onpageload = function() {
		// 배정규칙 readOnly false

		// 버튼 숨김
		btn_save.show("");   // 저장 버튼 
		btn_edit.hide();     // 수정 버튼 제거
		btn_delete.hide();   // 삭제 버튼 제거

		//search_box
        var cptIbxName = $p.getComponentById("searchComp_1");
        if(cptIbxName == undefined){
			search_box.hide();
        }

		// 자동 배정 설정 조회
		scwin.loadAutoAssignConfig();

		// 배정규칙 조회
		scwin.btn_search_onclick();
	};

	/************************** 이벤트 함수 *************************/

	/**
	 * 자동 배정 설정 로드
	 */
	scwin.loadAutoAssignConfig = function() {
		$c.sbm.execute($p, sbm_getAutoAssignConfig);
	};

	/**
	 * 자동 배정 설정 조회 콜백
	 */
	scwin.sbm_getAutoAssignConfig_submitdone = function(e) {
		var elData = e.responseJSON.elData;
		var elHeader = e.responseJSON.elHeader;

		if(elHeader == null || elHeader == "" || elHeader == "undefined" || elHeader.resSuc == false) {
			// 기본값 설정 (수동)
			dmp_autoAssignConfig.set("autoAssignEnabled", "false");
		}

		// 자동 배정 모드 UI 업데이트
		scwin.updateAutoAssignUI();
	};

	/**
	 * 자동 배정 UI 업데이트
	 */
	scwin.updateAutoAssignUI = function() {
		var isAutoEnabled = dmp_autoAssignConfig.get("autoAssignEnabled") === "true";

		// 자동/수동 모드 표시 업데이트
		if (isAutoEnabled) {
			spn_autoAssignMode.setValue("자동");
			spn_autoAssignMode.removeClass("status-manual");
			spn_autoAssignMode.addClass("status-auto");
		} else {
			spn_autoAssignMode.setValue("수동");
			spn_autoAssignMode.removeClass("status-auto");
			spn_autoAssignMode.addClass("status-manual");
		}
	};

	/**
	 * 자동 배정 모드 토글
	 */
	scwin.btn_toggleAutoAssign_onclick = function(e) {
		var currentMode = dmp_autoAssignConfig.get("autoAssignEnabled");
		var newMode = (currentMode === "true") ? "false" : "true";
		var modeText = (newMode === "true") ? "자동" : "수동";

		var promise = $c.win.confirm("자동 배정 모드를 '" + modeText + "'으로 변경하시겠습니까?");
		promise.then(function(result) {
			if (result) {
				dmp_autoAssignConfig.set("autoAssignEnabled", newMode);
				$c.sbm.execute($p, sbm_updateAutoAssignConfig);
			}
		});
	};

	/**
	 * 자동 배정 설정 업데이트 콜백
	 */
	scwin.sbm_updateAutoAssignConfig_submitdone = function(e) {
		var elHeader = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefined" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		} else {
			$c.win.alert("자동 배정 설정이 변경되었습니다.");
			scwin.updateAutoAssignUI();
		}
	};

	/**
	 * 조회 버튼 클릭 이벤트
	 */		   
	scwin.btn_search_onclick = function(e) {
		// PGAE번호 초기화
		dmp_assignRuleVo.set("pageIndex","1");
		pgl_assignRuleList.setSelectedIndex(1);

		// 서버통신 - 배정규칙 조회
		$c.sbm.execute($p, sbm_selectAssignRuleVoList);
	};

	scwin.sbm_assignRuleList_submitdone = function(e) {
		// error 수신시
		var elData = e.responseJSON.elData;
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}

		// 배정규칙 dataMap 초기화
		dmp_assignRuleVoDetail.setEmptyValue();

		// 배정규칙 readOnly false
		grp_assignRuleInfo.setReadOnly(false);

		// 버튼 숨김
		btn_save.show("");   // 저장 버튼
		btn_edit.hide();     // 수정 버튼 제거
		btn_delete.hide();   // 삭제 버튼 제거		

		// 총 레코드 수 업데이트
        spn_listCnt.setLabel(elData.totalCount);

        // 데이터를 그리드에 셋팅
        dlt_assignRuleVoList.setJSON(elData.assignRuleVoList);

        // 페이지 네비게이터 업데이트
        var totalPageCount = Math.ceil(elData.totalCount / dmp_assignRuleVo.get("pageSize"));
        pgl_assignRuleList.setCount(totalPageCount);

        spn_registerFlag.setValue("등록");

		// 페이징 정보 업데이트
        spn_totalPageCount.setValue(totalPageCount);
        spn_pageIndex.setValue(dmp_assignRuleVo.get("pageIndex"));
        pgl_assignRuleList.setSelectedIndex(dmp_assignRuleVo.get("pageIndex"));
	};	

	/**
	 * 페이지 번호 클릭 이벤트
	 */
	scwin.pgl_assignRuleList_onclick = function(idx) {
		// 페이지 번호 세팅
		dmp_assignRuleVo.set("pageIndex", idx);

		// 서버통신 - 배정규칙 조회
		$c.sbm.execute($p, sbm_selectAssignRuleVoList);
	};

	// 배정규칙 clear
	scwin.btn_reset_onclick = function(e) {
		// 배정규칙 dataMap 초기화
		dmp_assignRuleVoDetail.setEmptyValue();
		// 배정규칙 readOnly false
		grp_assignRuleInfo.setReadOnly(false);

		btn_save.show("");   // 저장 버튼 생성
		btn_edit.hide();     // 수정 버튼 제거
		btn_delete.hide();   // 삭제 버튼 제거

		spn_registerFlag.setValue("등록");
	};

	/**
	 * 그리드 셀 클릭 이벤트
	 */
	scwin.grd_list_oncellclick = function(row,col,colId) {
		dmp_assignRuleVoDetail.setJSON(dlt_assignRuleVoList.getRowJSON(row));

		btn_save.hide(); 		// 저장 버튼 제거
		btn_edit.show("");      // 수정 버튼 생성
		btn_delete.show("");    // 삭제 버튼 생성

		spn_registerFlag.setValue("수정");
	};

	/**
	 * 저장 버튼 클릭 이벤트
	 */	
	scwin.btn_save_onclick = function(e) {
		// 유효성 체크
		var valInfo = [
			{ id: "rule_id", mandatory: true }
		];

		if ($c.data.validateGroup(grp_assignRuleInfo, valInfo) === true) {
			var promise = $c.win.confirm("저장하시겠습니까?");
			promise.then(function(result) {  
				if (result){
					$c.sbm.execute(sbm_insertAssignRuleVo);
				}
			})
		} 	
	};

	/**
	 * 사원정보 등록 콜백
	 */
	scwin.sbm_insertAssignRuleVo_submitdone = function(e) {
		// error 수신시
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}else{
			// 등록되었습니다. msg 출력
			$c.win.alert("등록되었습니다.");
		}

		// 재조회
		scwin.btn_search_onclick();
	};

	/**
	 * 수정 버튼 클릭 이벤트
	 */		
	scwin.btn_edit_onclick = function(e) {
		// 유효성 체크
		var valInfo = [
			{ id: "rule_id", mandatory: true }
		];

		if ($c.data.validateGroup(grp_assignRuleInfo, valInfo) === true) {
			var promise = $c.win.confirm("수정하시겠습니까?");
			promise.then(function(result) {  
				if (result){
					$c.sbm.execute(sbm_updateAssignRuleVo);
				}
			})
		} 
	};

	/**
	 * 사원정보 수정 콜백
	 */
	scwin.sbm_updateAssignRuleVo_submitdone = function(e) {
		// error 수신시
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}else{
			// 수정되었습니다. msg 출력
			$c.win.alert("수정되었습니다.");
		}
		// 재조회
		scwin.btn_search_onclick();
	};

	/**
	 * 삭제 버튼 클릭 이벤트
	 */		
	scwin.btn_delete_onclick = function(e) {
		// 유효성 체크
		var valInfo = [
			{ id: "rule_id", mandatory: true }
		];

		if ($c.data.validateGroup(grp_assignRuleInfo, valInfo) === true) {
			var promise = $c.win.confirm("삭제하시겠습니까?");
			promise.then(function(result) {  
				if (result){
					$c.sbm.execute(sbm_deleteAssignRuleVo);
				}
			})
		}	
	};

	/**
	 * 삭제 콜백
	 */
	scwin.sbm_deleteAssignRuleVo_submitdone = function(e) {
		// error 수신시
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}else{
			// 삭제되었습니다. msg 출력
			$c.win.alert("삭제되었습니다.");
		}

		// 재조회
		scwin.btn_search_onclick();
	};
