scwin.sel_assignee_onchange = function(e) {
    const selectedAssignee = sel_assignee.getValue();
    dm_change.set("new_assignee", selectedAssignee);
};

scwin.btn_cancel_onclick = function(e) {
    // 팝업 닫기
    $p.closePopup();
};

