// CLAIM_RESULT 데이터 준비 함수
scwin.prepareClaimResultData = function () {
    var memo = txt_examMemo.getValue() || "";
    var refundAmount = dma_settlementSummary.get("total_refund_amt") || "0";

    // 현재 날짜 (YYYY-MM-DD 형식)
    var currentDate = new Date();
    var formattedDate = currentDate.getFullYear() + '-' +
        String(currentDate.getMonth() + 1).padStart(2, '0') + '-' +
        String(currentDate.getDate()).padStart(2, '0');

    // CLAIM_RESULT 데이터 설정
    dma_claimResult.set("claim_no", scwin.claim.claimNo);
    dma_claimResult.set("claim_memo", memo.trim());
    dma_claimResult.set("amount", refundAmount);
    dma_claimResult.set("date", formattedDate);

    return {
        claim_no: scwin.claim.claimNo,
        memo: memo.trim(),
        amount: refundAmount,
        date: formattedDate
    };
};

// 최종 심사 완료 (수정된 함수)

scwin.btn_complete_onclick = function () {
    // 기본 권한 체크
    if (!scwin.checkPermission("심사 완료")) return;

    // 상태별 세부 권한 체크
    // if (!scwin.checkCompletePermission()) return;

    var memo = txt_examMemo.getValue();

    if (!memo || memo.trim() === "") {
        scwin.toastAlert({
            icon: 'warning',
            title: '심사 메모를 먼저 입력해주세요.'
        });

        return;
    }

    if (!scwin.claim.claimNo) {
        // Swal.fire({
        //     title: '오류',
        //     text: '청구번호가 없습니다.',
        //     icon: 'error',
        //     confirmButtonText: '확인',
        //     confirmButtonColor: '#3085d6'
        // });

        scwin.noBtnAlertWithTimer({
            title: '오류',
            icon: 'error',
            text: result.message || '청구번호가 없습니다.'
        });

        return;
    }

    // CLAIM_RESULT 데이터 준비
    var resultData = scwin.prepareClaimResultData();

    // Swal.fire({
    //     title: '최종 심사 완료',
    //     html: '최종 심사를 완료하시겠습니까?<br><br>' +
    //         '• 청구번호: ' + resultData.claim_no + '<br>' +
    //         '• 지급금액: ' + resultData.amount + '원<br>' +
    //         '• 처리일자: ' + resultData.date,
    //     icon: 'question',
    //     showCancelButton: true,
    //     confirmButtonColor: '#3085d6',
    //     cancelButtonColor: '#d33',
    //     confirmButtonText: '완료',
    //     cancelButtonText: '취소'
    // })
        scwin.twoBtnAlert({
            title: '최종 심사 완료',
            icon: 'question',
            text: '청구번호가 없습니다.',
            html: '<div style="text-align: left;">' +
                '최종 심사를 완료하시겠습니까?<br><br>' +
                '• 청구번호: ' + resultData.claim_no + '<br>' +
                '• 지급금액: ' + resultData.amount + '원<br>' +
                '• 처리일자: ' + resultData.date +
                '</div>',
        })
    .then((result) => {
        if (result.isConfirmed) {
            scwin.showLoading(true);
            $p.executeSubmission("sbm_saveClaimResult");
            localStorage.removeItem("claim");
            location.href = "/InsWebApp/websquare/websquare.html?w2xPath=/InsWebApp/ui/webWrapper.xml"
        }
    });
};


