// 청구 상태 설정 함수 추가
scwin.setClaimStatus = function () {
    var status = scwin.claim.status;

    if (status) {
        // spn_status에 상태 값 설정
        spn_status.setValue(status);

        // 상태별 CSS 클래스 설정
        scwin.setStatusStyle(status);
    }
};

// 상태별 스타일 설정 함수
scwin.setStatusStyle = function (status) {
    // 기존 상태 클래스 제거
    spn_status.removeClass("status_pending");
    spn_status.removeClass("status_completed");
    spn_status.removeClass("status_waiting");
    spn_status.removeClass("status_supplement");
    spn_status.removeClass("status_return");
    spn_status.removeClass("status_approval");
    spn_status.removeClass("status_reject");

    // 상태별 클래스 추가
    switch (status) {
        case "보완":
            spn_status.addClass("status_supplement");
            break;
        case "대기":
            spn_status.addClass("status_waiting");
            break;
        case "결재완료":
            spn_status.addClass("status_completed");
            break;
        case "결재반려":
            spn_status.addClass("status_reject");
            break;
        case "완료":
            spn_status.addClass("status_completed");
            break;
        case "결재중":
            spn_status.addClass("status_approval");
            break;
        case "반송":
            spn_status.addClass("status_return");
            break;
        default:
            spn_status.addClass("status_pending");
            break;
    }
};

// 상태별 권한 설정 함수 추가
scwin.setStatusBasedPermissions = function (status) {
    console.log("상태별 권한 설정:", status);

    if (status === "결재중") {
        // 결재중 상태: 보완, 반송 버튼 비활성화, 결재신청도 비활성화
        console.log("결재중 상태 - 보완, 반송, 결재신청 비활성화");

        btn_supplement.setDisabled(true);
        btn_supplement.addClass("disabled-button");

        btn_return.setDisabled(true);
        btn_return.addClass("disabled-button");

        btn_approval.setDisabled(true);
        btn_approval.addClass("disabled-button");

        // 메모 수정도 제한
        txt_examMemo.setReadOnly(true);
        txt_examMemo.addClass("readonly-textarea");

        btn_generateMemo.setDisabled(true);
        btn_generateMemo.addClass("disabled-button");

        btn_complete.setDisabled(true);
        btn_complete.addClass("disabled-button");
    } else if (status === "결재완료") {
        // 결재완료 상태: 모든 수정 작업 비활성화, 완료 버튼만 활성화
        console.log("결재완료 상태 - 대부분의 작업 비활성화, 완료만 가능");

        btn_supplement.setDisabled(true);
        btn_supplement.addClass("disabled-button");

        btn_return.setDisabled(true);
        btn_return.addClass("disabled-button");

        btn_approval.setDisabled(true);
        btn_approval.addClass("disabled-button");

        btn_generateMemo.setDisabled(true);
        btn_generateMemo.addClass("disabled-button");

        // 메모 수정 제한
        txt_examMemo.setReadOnly(true);
        txt_examMemo.addClass("readonly-textarea");

        // 완료 버튼은 활성화 유지 (본인 청구건인 경우)
        if (scwin.employee.role === "실무자" && scwin.claim.empNo === scwin.employee.empNo) {
            btn_complete.setDisabled(false);
            btn_complete.removeClass("disabled-button");
        }

    } else if (status === "결재반려") {
        // 결재반려 상태: 모든 작업 가능 (재작업을 위해)
        console.log("결재반려 상태 - 모든 작업 가능 (재작업)");

        if (scwin.employee.role === "실무자" && scwin.claim.empNo === scwin.employee.empNo) {
            btn_supplement.setDisabled(false);
            btn_supplement.removeClass("disabled-button");

            btn_return.setDisabled(false);
            btn_return.removeClass("disabled-button");

            btn_approval.setDisabled(false);
            btn_approval.removeClass("disabled-button");

            btn_complete.setDisabled(false);  // 결재반려 상태에서도 심사완료 가능
            btn_complete.removeClass("disabled-button");

            btn_generateMemo.setDisabled(false);
            btn_generateMemo.removeClass("disabled-button");

            txt_examMemo.setReadOnly(false);
            txt_examMemo.removeClass("readonly-textarea");
        }

    } else if (status === "완료") {
        // 완료 상태: 모든 수정 작업 비활성화 (조회만 가능)
        console.log("완료 상태 - 모든 수정 작업 비활성화");

        btn_supplement.setDisabled(true);
        btn_supplement.addClass("disabled-button");

        btn_return.setDisabled(true);
        btn_return.addClass("disabled-button");

        btn_approval.setDisabled(true);
        btn_approval.addClass("disabled-button");

        btn_complete.setDisabled(true);
        btn_complete.addClass("disabled-button");

        btn_generateMemo.setDisabled(true);
        btn_generateMemo.addClass("disabled-button");

        txt_examMemo.setReadOnly(true);
        txt_examMemo.addClass("readonly-textarea");

    } else if (status === "보완" || status === "반송") {
        // 보완/반송 상태: 제한적 작업만 가능
        console.log(status + " 상태 - 제한적 작업 가능");

        if (scwin.employee.role === "실무자" && scwin.claim.empNo === scwin.employee.empNo) {
            // 보완/반송 상태에서는 재신청을 위한 작업만 가능
            btn_supplement.setDisabled(true);
            btn_supplement.addClass("disabled-button");

            btn_return.setDisabled(true);
            btn_return.addClass("disabled-button");

            // 결재신청과 메모 생성, 심사완룼도 가능
            btn_approval.setDisabled(false);
            btn_approval.removeClass("disabled-button");

            btn_complete.setDisabled(false);  // 보완/반송 상태에서도 심사완료 가능
            btn_complete.removeClass("disabled-button");

            btn_generateMemo.setDisabled(false);
            btn_generateMemo.removeClass("disabled-button");

            txt_examMemo.setReadOnly(false);
            txt_examMemo.removeClass("readonly-textarea");
        }

    } else if (status === "대기") {
        // 대기 상태: 모든 작업 가능 (심사완료 포함)
        console.log("대기 상태 - 모든 작업 가능");

        if (scwin.employee.role === "실무자" && scwin.claim.empNo === scwin.employee.empNo) {
            btn_supplement.setDisabled(false);
            btn_supplement.removeClass("disabled-button");

            btn_return.setDisabled(false);
            btn_return.removeClass("disabled-button");

            btn_approval.setDisabled(false);
            btn_approval.removeClass("disabled-button");

            btn_complete.setDisabled(false);  // 대기 상태에서도 심사완료 가능
            btn_complete.removeClass("disabled-button");

            btn_generateMemo.setDisabled(false);
            btn_generateMemo.removeClass("disabled-button");

            txt_examMemo.setReadOnly(false);
            txt_examMemo.removeClass("readonly-textarea");
        }

    } else {
        // 기타 상태 또는 상태가 없는 경우: 기본 권한 적용
        console.log("기타/기본 상태 - 기본 권한 적용");

        if (scwin.employee.role === "실무자" && scwin.claim.empNo === scwin.employee.empNo) {
            btn_supplement.setDisabled(false);
            btn_supplement.removeClass("disabled-button");

            btn_return.setDisabled(false);
            btn_return.removeClass("disabled-button");

            btn_approval.setDisabled(false);
            btn_approval.removeClass("disabled-button");

            btn_generateMemo.setDisabled(false);
            btn_generateMemo.removeClass("disabled-button");

            txt_examMemo.setReadOnly(false);
            txt_examMemo.removeClass("readonly-textarea");
        }
    }

    // 상태 정보 표시 업데이트
    scwin.updateStatusInfo(status);

    // 관리자 권한 재설정 (상태별 권한 후에 실행)
    if (scwin.employee && scwin.employee.role !== "실무자") {
        if (status === "결재중") {
            // 결재중 상태에서는 관리자 버튼들 활성화
            btn_approve_complete.setDisabled(false);
            btn_approve_complete.removeClass("disabled-button");
            btn_reject.setDisabled(false);
            btn_reject.removeClass("disabled-button");
        } else {
            // 다른 상태에서는 관리자 버튼들 비활성화
            btn_approve_complete.setDisabled(true);
            btn_approve_complete.addClass("disabled-button");
            btn_reject.setDisabled(true);
            btn_reject.addClass("disabled-button");
        }
    }
};
// 상태 정보 업데이트 함수 추가
scwin.updateStatusInfo = function (status) {
    var statusMessage = "";

    if (status === "결재중") {
        statusMessage = "현재 결재 진행 중입니다. 수정이 제한됩니다.";
    } else if (status === "결재완료") {
        statusMessage = "결재가 완료되었습니다. 최종 심사 완료만 가능합니다.";
    } else if (status === "결재반려") {
        statusMessage = "결재가 반려되었습니다. 재작업 후 다시 신청해주세요.";
    } else if (status === "완료") {
        statusMessage = "심사가 완료되었습니다. 수정이 불가능합니다.";
    } else if (status === "보완") {
        statusMessage = "보완 요청 상태입니다. 보완 후 재신청해주세요.";
    } else if (status === "반송") {
        statusMessage = "반송 상태입니다. 수정 후 재신청해주세요.";
    } else if (status === "대기") {
        statusMessage = "심사 대기 중입니다.";
    }

    // 상태 메시지를 화면에 표시 (선택적)
    if (statusMessage) {
        console.log("상태 메시지:", statusMessage);
        // 필요시 화면에 표시할 수 있음
        // alert(statusMessage);
    }
};
// 상태별 권한 체크 함수 세분화
scwin.checkStatusPermission = function (actionName) {
    var status = scwin.claim.status;

    console.log("권한 체크 - 액션:", actionName, "상태:", status);

    if (status === "결재중") {
        if (actionName === "반송" || actionName === "보완 요청" || actionName === "결재 신청" || actionName === "메모 생성") {
            scwin.noBtnAlertWithTimer({
                title: '작업 제한',
                text: '현재 결재 진행 중이므로 ' + actionName + '을(를) 할 수 없습니다.',
                icon: 'info',
            });
            return false;
        }

    } else if (status === "결재완료") {
        if (actionName === "반송" || actionName === "보완 요청" || actionName === "결재 신청" || actionName === "메모 생성") {
            Swal.fire({
                title: '작업 제한',
                text: '결재가 완료된 상태입니다. ' + actionName + '을(를) 할 수 없습니다.',
                icon: 'info',
                confirmButtonText: '확인',
                confirmButtonColor: '#3085d6'
            });
            return false;
        }

    } else if (status === "완료") {
        if (actionName === "반송" || actionName === "보완 요청" || actionName === "결재 신청" ||
            actionName === "메모 생성" || actionName === "심사 완료") {
            Swal.fire({
                title: '작업 불가',
                text: '이미 심사가 완료된 건입니다. ' + actionName + '을(를) 할 수 없습니다.',
                icon: 'warning',
                confirmButtonText: '확인',
                confirmButtonColor: '#3085d6'
            });
            return false;
        }

    } else if (status === "보완") {
        if (actionName === "반송" || actionName === "보완 요청") {
           scwin.noBtnAlertWithTimer({
                title: '보완 진행 중',
                text: '현재 보완 요청 상태입니다. 보완 완료 후 재신청해주세요.',
                icon: 'info',
            });
            return false;
        }

    } else if (status === "반송") {
        if (actionName === "반송" || actionName === "보완 요청") {
            scwin.noBtnAlertWithTimer({
                title: '반송 상태',
                text: '현재 반송 상태입니다. 수정 완료 후 재신청해주세요.',
                icon: 'info',
                confirmButtonText: '확인',
                confirmButtonColor: '#3085d6'
            });
            return false;
        }
    }

    return true;
};


// 각 버튼별 상세 권한 체크 함수들
scwin.checkReturnPermission = function () {
    var status = scwin.claim.status;
    var restrictedStatuses = ["결재중", "결재완료", "완료", "보완", "반송"];

    if (restrictedStatuses.includes(status)) {
        var message = "";
        var title = "";

        if (status === "결재중") {
            title = "결재 진행 중";
            message = "결재 진행 중에는 반송할 수 없습니다.";
        } else if (status === "결재완료") {
            title = "결재 완료";
            message = "결재 완료된 건은 반송할 수 없습니다.";
        } else if (status === "완료") {
            title = "심사 완료";
            message = "완료된 건은 반송할 수 없습니다.";
        } else if (status === "보완") {
            title = "보완 진행 중";
            message = "이미 보완 요청된 건입니다.";
        } else if (status === "반송") {
            title = "반송 상태";
            message = "이미 반송된 건입니다.";
        }

        Swal.fire({
            title: title,
            text: message,
            icon: 'warning',
            confirmButtonText: '확인',
            confirmButtonColor: '#3085d6'
        });
        return false;
    }

    return true;
};

scwin.checkSupplementPermission = function () {
    var status = scwin.claim.status;
    var restrictedStatuses = ["결재중", "결재완료", "완료", "보완", "반송"];

    if (restrictedStatuses.includes(status)) {
        var message = "";
        var title = "";

        if (status === "결재중") {
            title = "결재 진행 중";
            message = "결재 진행 중에는 보완 요청할 수 없습니다.";
        } else if (status === "결재완료") {
            title = "결재 완료";
            message = "결재 완료된 건은 보완 요청할 수 없습니다.";
        } else if (status === "완료") {
            title = "심사 완료";
            message = "완료된 건은 보완 요청할 수 없습니다.";
        } else if (status === "보완") {
            title = "보완 진행 중";
            message = "이미 보완 요청된 건입니다.";
        } else if (status === "반송") {
            title = "반송 상태";
            message = "반송된 건은 먼저 수정해주세요.";
        }

        scwin.noBtnAlertWithTimer({
            title: title,
            text: message,
            icon: 'warning',
        });
        return false;
    }

    return true;
};

scwin.checkApprovalPermission = function () {
    var status = scwin.claim.status;
    var restrictedStatuses = ["결재중", "결재완료", "완료"];

    if (restrictedStatuses.includes(status)) {
        var message = "";
        var title = "";

        if (status === "결재중") {
            title = "결재 진행 중";
            message = "이미 결재 진행 중입니다.";
        } else if (status === "결재완료") {
            title = "결재 완료";
            message = "이미 결재 완료된 건입니다.";
        } else if (status === "완료") {
            title = "심사 완료";
            message = "완료된 건은 결재신청할 수 없습니다.";
        }

        Swal.fire({
            title: title,
            text: message,
            icon: 'info',
            confirmButtonText: '확인',
            confirmButtonColor: '#3085d6'
        });
        return false;
    }

    return true;
};

