head.script:
scwin.onpageload = function() {
	// todo: 관리자/ 실무자에 따라 분기처리
    // parent.WebSquare.util.getComponentById("web_container").setSrc("/InsWebApp/ui/audit/table-test.xml");
};

function selectMenu(menuId) {
    const allItems = ["my_claim", "emp_now", "emp_manage", "search_claimNum", "search_personNum", "vacation"];

    allItems.forEach(id => {
        const comp = $w.getComponentById(id);
        comp.removeClass("selected");
    });

    const selectedComp = $w.getComponentById(menuId);
    selectedComp.addClass("selected");
}

function movePage(link) {
    parent.WebSquare.util.getComponentById("web_container").setSrc(link);
}

scwin.my_claim_onclick = function () {
    selectMenu("my_claim");
};

scwin.emp_now_onclick = function () {
    selectMenu("emp_no");
};

scwin.emp_manage_onclick = function () {
    selectMenu("emp_manage");
};

scwin.search_claimNum_onclick = function () {
    selectMenu("search_claimNum");
};

scwin.search_personNum_onclick = function () {
    selectMenu("search_personNum");
};

scwin.vacation_onclick = function () {
    selectMenu("vacation");
};
