head.script:
scwin.onpageload = function() {
	dashboard.addClass("selected");
};

function removeSClass() {
    const elements = document.getElementsByClassName("selected");
    for (let i = 0; i < elements.length; i++) {
        elements[i].classList.remove("selected");
    }
}

function movePage(link) {
    parent.WebSquare.util.getComponentById("web_container").setSrc(link);
}

scwin.dashboard_onclick = function () {
    removeSClass();
    dashboard.addClass("selected");
    movePage("/InsWebApp/ui/dashBoard/dashBoard7.xml");
};

scwin.my_claim_onclick = function () {
    removeSClass();
    my_claim.addClass("selected");

    let role = JSON.parse(localStorage.getItem("employee")).role;
    if (role === "실무자") {
        movePage("/InsWebApp/ui/claim/my-claims.xml");
    } else {
        movePage("/InsWebApp/ui/approval/my-approval.xml")
    }
};

scwin.search_claimNum_onclick = function () {
    removeSClass();
    search_claimNum.addClass("selected");
        let role = JSON.parse(localStorage.getItem("employee")).role;
    if (role === "실무자") {
        movePage("/InsWebApp/ui/assignment/claimListSearch.xml");
    } else {
        movePage("/InsWebApp/ui/approval/my-approval.xml")
    }
};

scwin.search_personNum_onclick = function () {
    removeSClass();
    search_personNum.addClass("selected");
    movePage("/InsWebApp/ui/employee/SearchRrn.xml");
};

scwin.vacation_onclick = function () {
    removeSClass();
    vacation.addClass("selected");
};
