head.script:
	scwin.onpageload = function() {
		// 부서정보 readOnly false

		// 버튼 숨김
		btn_save.show("");   // 저장 버튼 
		btn_edit.hide();     // 수정 버튼 제거
		btn_delete.hide();   // 삭제 버튼 제거

		/////////////////////////////////////////////
		// 페이지 크기 설정 영역 / 웹관리자 화면 page 생성 시 주석 해제
		/////////////////////////////////////////////
		//sel_pageSize.setValue("10");

		//search_box
        var cptIbxName = $p.getComponentById("searchComp_1");
        if(cptIbxName == undefined){
			search_box.hide();
        }

		// 부서정보 조회
		scwin.btn_search_onclick();
	};

	/************************** 이벤트 함수 *************************/

	/**
	 * 조회 버튼 클릭 이벤트
	 * @date
	 * @memberOf
	 * @param 
	 * @returns
	 * @author 
	 * @example
	 */		   
	scwin.btn_search_onclick = function(e) {

		// PGAE번호 초기화
		dmp_insDeptVo.set("pageIndex","1");
		pgl_insDeptList.setSelectedIndex(1);

		// 서버통신 - 부서정보 조회
		$c.sbm.execute($p, sbm_selectInsDeptVoList);
	};

	scwin.sbm_insDeptList_submitdone = function(e) {

		// error 수신시
		var elData = e.responseJSON.elData;
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}

		// 부서정보 dataMap 초기화
		dmp_insDeptVoDetail.setEmptyValue();

		// 부서정보 readOnly false
		grp_insDeptInfo.setReadOnly(false);

		// 버튼 숨김
		btn_save.show("");   // 저장 버튼
		btn_edit.hide();     // 수정 버튼 제거
		btn_delete.hide();   // 삭제 버튼 제거		

		// 총 레코드 수 업데이트
        spn_listCnt.setLabel(elData.totalCount);

        // 데이터를 그리드에 셋팅
        dlt_insDeptVoList.setJSON(elData.insDeptVoList);

        // 페이지 네비게이터 업데이트
        var totalPageCount = Math.ceil(elData.totalCount / dmp_insDeptVo.get("pageSize"));
        pgl_insDeptList.setCount(totalPageCount);

        spn_registerFlag.setValue("등록");

		// 페이징 정보 업데이트
        spn_totalPageCount.setValue(totalPageCount);
        spn_pageIndex.setValue(dmp_insDeptVo.get("pageIndex"));
        pgl_insDeptList.setSelectedIndex(dmp_insDeptVo.get("pageIndex"));
	};	

	/**
	 * 페이지 번호 클릭 이벤트
	 * @date
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */
	scwin.pgl_insDeptList_onclick = function(idx) {

		// 페이지 번호 세팅
		dmp_insDeptVo.set("pageIndex", idx);

		/////////////////////////////////////////////
		// 페이지 크기 설정 영역 / 웹관리자 화면 page 생성 시 주석 해제
		/////////////////////////////////////////////
		//dmp_insDeptVo.set("pageSize", sel_pageSize.getValue());

		// 서버통신 - 부서정보 조회
		$c.sbm.execute($p, sbm_selectInsDeptVoList);
	};

	// 부서정보 clear
	scwin.btn_reset_onclick = function(e) {

		// 부서정보 dataMap 초기화
		dmp_insDeptVoDetail.setEmptyValue();
		// 부서정보 readOnly false
		grp_insDeptInfo.setReadOnly(false);

		btn_save.show("");   // 저장 버튼 생성
		btn_edit.hide();     // 수정 버튼 제거
		btn_delete.hide();   // 삭제 버튼 제거

		spn_registerFlag.setValue("등록");
	};

	/**
	 * 그리드 셀 클릭 이벤트
	 * @date
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */
	scwin.grd_list_oncellclick = function(row,col,colId) {

		dmp_insDeptVoDetail.setJSON(dlt_insDeptVoList.getRowJSON(row));

		btn_save.hide(); 		// 저장 버튼 제거
		btn_edit.show("");      // 수정 버튼 생성
		btn_delete.show("");    // 삭제 버튼 생성

		spn_registerFlag.setValue("수정");
	};

	/**
	 * 저장 버튼 클릭 이벤트
	 * @date
	 * @memberOf
	 * @param 
	 * @returns
	 * @author 
	 * @example
	 */	
	scwin.btn_save_onclick = function(e) {

		// 유효성 체크
		var valInfo = [
			{ id: "dept_id", mandatory: true }

		];

		if ($c.data.validateGroup(grp_insDeptInfo, valInfo) === true) {
			var promise = $c.win.confirm("저장하시겠습니까?");
			promise.then(function(result) {  
				if (result){
					$c.sbm.execute(sbm_insertInsDeptVo);
				}
			})
		} 	
	};

	/**
	 * 사원정보 등록 콜백
	 * @date 
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */
	scwin.sbm_insertInsDeptVo_submitdone = function(e) {

		// error 수신시
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}else{
			// 등록되었습니다. msg 출력
			$c.win.alert("등록되었습니다.");
		}

		// 재조회
		scwin.btn_search_onclick();
	};

	/**
	 * 수정 버튼 클릭 이벤트
	 * @date
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */		
	scwin.btn_edit_onclick = function(e) {

		// 유효성 체크
		var valInfo = [
			{ id: "dept_id", mandatory: true }

		];

		if ($c.data.validateGroup(grp_insDeptInfo, valInfo) === true) {
			var promise = $c.win.confirm("수정하시겠습니까?");
			promise.then(function(result) {  
				if (result){
					$c.sbm.execute(sbm_updateInsDeptVo);
				}
			})
		} 
	};

	/**
	 * 사원정보 수정 콜백
	 * @date 
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */
	scwin.sbm_updateInsDeptVo_submitdone = function(e) {

		// error 수신시
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}else{
			// 수정되었습니다. msg 출력
			$c.win.alert("수정되었습니다.");
		}
		// 재조회
		scwin.btn_search_onclick();
	};

	/**
	 * 삭제 버튼 클릭 이벤트
	 * @date
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */		
	scwin.btn_delete_onclick = function(e) {

		// 유효성 체크
		var valInfo = [
			{ id: "dept_id", mandatory: true }

		];

		if ($c.data.validateGroup(grp_insDeptInfo, valInfo) === true) {
			var promise = $c.win.confirm("삭제하시겠습니까?");
			promise.then(function(result) {  
				if (result){
					$c.sbm.execute(sbm_deleteInsDeptVo);
				}
			})
		}	
	};

	/**
	 * 삭제 콜백
	 * @date 
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */
	scwin.sbm_deleteInsDeptVo_submitdone = function(e) {

		// error 수신시
		var elHeader  = e.responseJSON.elHeader;
		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			$c.win.alert(`에러코드 : ${elHeader.resCode}\n에러메시지 : ${elHeader.resMsg}`);
			return false;
		}else{
			// 삭제되었습니다. msg 출력
			$c.win.alert("삭제되었습니다.");
		}

		// 재조회
		scwin.btn_search_onclick();
	};
