head.script:
scwin.onpageload = function() {
    // 1. URL이 아닌 로컬 스토리지에서 "id" 키로 저장된 값을 가져옵니다.
    const storedId = localStorage.getItem("userId");

    // 2. scwin.currentUserId 변수에 저장합니다.
    scwin.currentUserId = storedId;

    // 3. ID가 제대로 저장되었는지 확인합니다.
    console.log("로컬 스토리지에서 가져온 사용자 ID:", scwin.currentUserId);

    // 4. 만약 ID가 없다면 에러 처리를 합니다.
    if (!scwin.currentUserId) {
        alert("사용자 정보를 가져올 수 없습니다. 다시 로그인해주세요.");
        return;
    }

    // 5. 비밀번호와 UI를 초기화합니다.
    scwin.password = "";
    scwin.updateCircles();

};

scwin.button1_onclick = function (e) {
    scwin.addDigit('1');
};

scwin.button2_onclick = function (e) {
    scwin.addDigit('2');
};

scwin.button3_onclick = function (e) {
    scwin.addDigit('3');
};

scwin.button4_onclick = function (e) {
    scwin.addDigit('4');
};

scwin.button5_onclick = function (e) {
    scwin.addDigit('5');
};

scwin.button6_onclick = function (e) {
    scwin.addDigit('6');
};

scwin.button7_onclick = function (e) {
    scwin.addDigit('7');
};

scwin.button8_onclick = function (e) {
    scwin.addDigit('8');
};

scwin.button9_onclick = function (e) {
    scwin.addDigit('9');
};

scwin.button0_onclick = function (e) {
    scwin.addDigit('0');
};

// 공통 함수
scwin.addDigit = function(digit) {
    if (scwin.password.length < 6) {
        scwin.password += digit;
        scwin.updateCircles();

        if (scwin.password.length === 6) {
            console.log("비밀번호 입력 완료:", scwin.password);

            // 간편 로그인 검증
            scwin.performSimpleLogin();
        }
    }
};

scwin.performSimpleLogin = function() {
    try {
        // 간편 로그인 데이터 설정
        dma_login_request.set("loginType", "SIMPLE");  // 로그인 타입 설정
        dma_login_request.set("userId", scwin.currentUserId);  // 사용자 ID 설정
        dma_login_request.set("simplePw", scwin.password);   // 간편 비밀번호 설정

        let reqData = { "elData": dma_login_request.getJSON() };

        console.log("간편 로그인 요청 데이터:", reqData);

        // submission 실행 전 검증
        if (typeof sbm_SimpleLogin === 'undefined') {
            console.error("sbm_SimpleLogin이 정의되지 않았습니다.");
            alert("로그인 설정에 오류가 있습니다. 관리자에게 문의하세요.");
            return;
        }

        $c.sbm.execute(sbm_SimpleLogin, reqData);

    } catch (error) {
        console.error("간편 로그인 요청 실패:", error);
        alert("로그인 처리 중 오류가 발생했습니다.");
        scwin.resetPassword();
    }
};

scwin.updateCircles = function() {
    // 모든 동그라미 초기화
    const circles = ["circle1", "circle2", "circle3", "circle4", "circle5", "circle6"];

    circles.forEach(function(circleId) {
        const circle = $p.getComponentById(circleId);
        if (circle) {
            circle.removeClass("filled");
        }
    });

    // 입력된 개수만큼 채우기
    for (let i = 0; i < scwin.password.length && i < 6; i++) {
        const circle = $p.getComponentById(circles[i]);
        if (circle) {
            circle.addClass("filled");
        }
    }
};

scwin.resetPassword = function() {
    scwin.password = "";
    scwin.updateCircles();
};

scwin.sbm_SimpleLogin_submitdone = function (e) {
    var customerId = null;

    try {
        // 서버 응답에서 customerId를 찾아봅니다 (로그인 성공의 증거)
        if (e && e.responseJSON && e.responseJSON.dma_login_response) {
            customerId = e.responseJSON.dma_login_response.id;
        }

        // ✅ 로그인 성공 시 (customerId를 받아왔을 때)
        if (customerId) {
            localStorage.setItem("id", customerId);

            // 성공했으므로 메인 페이지로 이동합니다.
            location.href = "/InsWebApp/websquare/websquare.html?w2xPath=/InsWebApp/main/mainWrapper.xml";

        // ❌ 로그인 실패 시
        } else {
            alert("비밀번호가 일치하지 않습니다. 다시 시도해주세요.");
            scwin.resetPassword(); // 비밀번호 입력창 초기화
        }

    } catch (error) {
        alert("로그인 처리 중 오류가 발생했습니다.");
        scwin.resetPassword();
    }
};

scwin.sbm_SimpleLoginAdd_submitdone = function (e) {

};
