head.script:
scwin.onpageload = function() {
	scwin.password = "";
};

scwin.button1_onclick = function (e) {
    scwin.addDigit('1');
};

scwin.button2_onclick = function (e) {
    scwin.addDigit('2');
};

scwin.button3_onclick = function (e) {
     scwin.addDigit('3');
};

scwin.button4_onclick = function (e) {
    scwin.addDigit('4');
};

scwin.button5_onclick = function (e) {
    scwin.addDigit('5');
};

scwin.button6_onclick = function (e) {
    scwin.addDigit('6');
};

scwin.button7_onclick = function (e) {
    scwin.addDigit('7');
};

scwin.button8_onclick = function (e) {
    scwin.addDigit('8');
};

scwin.button9_onclick = function (e) {
    scwin.addDigit('9');
};

scwin.button0_onclick = function (e) {
    scwin.addDigit('0');
};


// 공통 함수
scwin.addDigit = function(digit) {
    if (scwin.password.length < 6) {
        scwin.password += digit;
        scwin.updateCircles();

        if (scwin.password.length === 6) {
            console.log("비밀번호 입력 완료:", scwin.password);

            dma_SimpleLogin.set("sp_pw", scwin.password);

            let reqData = { "elData": dma_SimpleLogin.getJSON() };

            $c.sbm.execute(sbm_SimpleLogin, reqData);
        }
    }
};

scwin.updateCircles = function() {
    // 모든 동그라미 초기화
    $p.getComponentById("circle1").removeClass("filled");
    $p.getComponentById("circle2").removeClass("filled");
    $p.getComponentById("circle3").removeClass("filled");
    $p.getComponentById("circle4").removeClass("filled");
    $p.getComponentById("circle5").removeClass("filled");
    $p.getComponentById("circle6").removeClass("filled");

    // 입력된 개수만큼 채우기
    if (scwin.password.length >= 1) $p.getComponentById("circle1").addClass("filled");
    if (scwin.password.length >= 2) $p.getComponentById("circle2").addClass("filled");
    if (scwin.password.length >= 3) $p.getComponentById("circle3").addClass("filled");
    if (scwin.password.length >= 4) $p.getComponentById("circle4").addClass("filled");
    if (scwin.password.length >= 5) $p.getComponentById("circle5").addClass("filled");
    if (scwin.password.length >= 6) $p.getComponentById("circle6").addClass("filled");
};


scwin.sbm_SimpleLogin_submitdone = function (e) {
    let elHeader = e.responseJSON.elHeader;
    if(elHeader != "" && elHeader != null && elHeader != "undefined") {	
        if(elHeader.resSuc == true) {
            location.href = "/InsWebApp/websquare/websquare.html?w2xPath=/InsWebApp/main/mainWrapper.xml"
            console.log("연결성공");
        }else{
            dma_SimpleLogin.setEmptyValue();
            alert("로그인 정보를 확인하세요");
        }
    }
};
